import React, { useState } from 'react';
import bgGif from './shared/art/rainy-10.gif';
import './shared/styles/style.css';
import Header from './widgets/header';
import Footer from './widgets/Footer';
import UpWeatherPc from './widgets/UpWeatherPc';
import WeatherManager from './widgets/WeatherManager';
import WeatherHighlights from './widgets/WeatherHighlights';
function App() {
  const [searchedCity, setSearchedCity] = useState('Lublin');

  const handleCitySearch = (city) => {
    console.log("Город для поиска:", city);
    setSearchedCity(city); // Обновляем состояние с новым городом
  };
  return (
    <div className="app-container">
      <img src={bgGif} alt="background" className="bg-video" />

          <Header onSearch={handleCitySearch}/>
          <UpWeatherPc Scity={searchedCity} />
          <WeatherManager city={searchedCity} />
          <WeatherHighlights city={searchedCity}/>
          {/* <UpWetherMobile/>

          <DownWeatherPc/>
          <DownWeatherMobile/> */}
          <Footer />



    </div>
  );
}

export default App;
