import React, { useState } from 'react';
import './shared/styles/style.css';

const Header = ( ) => {
  // Stany do kontrolowania widoczno≈õci okien
  const [langOpen, setLangOpen] = useState(false);
  const [friendsOpen, setFriendsOpen] = useState(false);

  return (
    <header className="header glass-panel">
      <div className="logo">WeatherApp</div>

      <div className="search-bar">
        <input type="text" placeholder="Szukaj miasta..." className="my-input" />
      </div>

      <div className="nav-buttons">
        {/* Przycisk Jƒôzyka */}
        <div className="dropdown-container">
          <button onClick={() => setLangOpen(!langOpen)} className="nav-btn">
            üåê Jƒôzyk
          </button>
          
          {/* Okno wyp≈ÇywajƒÖce z g√≥ry */}
          <div className={`lang-menu glass-panel ${langOpen ? 'active' : ''}`}>
            <button>Polski</button>
            <button>English</button>
          </div>
        </div>

        {/* Przycisk Znajomych */}
        <button onClick={() => setFriendsOpen(!friendsOpen)} className="nav-btn">
          üë• Znajomi
        </button>
      </div>

      {/* Okno wyp≈ÇywajƒÖce z prawej strony (Sidebar) */}
      <div className={`friends-sidebar glass-panel ${friendsOpen ? 'active' : ''}`}>
        <button className="close-btn" onClick={() => setFriendsOpen(false)}>√ó</button>
        <h3>Twoi Koledzy</h3>
        <div className="friend-card">
          <strong>Pawe≈Ç</strong>
          <p>Lublin: 2¬∞C</p>
        </div>
        <div className="friend-card">
          <strong>Ania</strong>
          <p>Warszawa: -1¬∞C</p>
        </div>
      </div>
    </header>
  );
};

export default Header;
