import React from 'react';

const cardStyle = { background: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '15px', color: '#fff' };
const titleStyle = { fontSize: '12px', opacity: 0.6, textTransform: 'uppercase' };

const WeatherHighlights = ({ data }) => {
  if (!data || !data.current) return null;
  const { current } = data;

  const getAirQualityLabel = (index) => {
    const levels = {
      1: { text: '≈öwietna', color: '#4ade80' },
      2: { text: 'Dobra', color: '#a3e635' },
      3: { text: '≈örednia', color: '#facc15' },
      4: { text: 'Z≈Ça', color: '#fb923c' },
      5: { text: 'Bardzo z≈Ça', color: '#f87171' },
      6: { text: 'Hazardowa', color: '#b91c1c' }
    };
    return levels[index] || { text: 'Brak danych', color: '#fff' };
  };

  const aqi = getAirQualityLabel(current.air_quality?.['us-epa-index']);

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
      {/* Wiatr */}
      <div className="card-glass" style={cardStyle}>
        <span style={titleStyle}>Wiatr i Kierunek</span>
        <div style={{ fontSize: '24px', fontWeight: 'bold', margin: '10px 0' }}>{current.wind_kph} km/h</div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <div style={{ transform: `rotate(${current.wind_degree}deg)`, fontSize: '20px' }}>‚¨ÜÔ∏è</div>
            <span style={{opacity: 0.8}}>{current.wind_dir}</span>
        </div>
      </div>

      {/* AQI & Wilgotno≈õƒá */}
      <div className="card-glass" style={cardStyle}>
        <span style={titleStyle}>Jako≈õƒá Powietrza</span>
        <div style={{ color: aqi.color, fontSize: '22px', fontWeight: 'bold', margin: '5px 0' }}>{aqi.text}</div>
        <div style={{ borderTop: '1px solid rgba(255,255,255,0.1)', marginTop: '10px', paddingTop: '10px' }}>
            <span style={titleStyle}>Wilgotno≈õƒá: {current.humidity}%</span>
        </div>
      </div>

      {/* Lifestyle (Warunki dla...) */}
      <div className="card-glass" style={cardStyle}>
        <span style={titleStyle}>Warunki Lifestyle</span>
        <div style={{ marginTop: '10px', fontSize: '14px' }}>
            <div>üèÉ Bieganie: {current.temp_c > 5 && current.wind_kph < 25 ? "Dobre warunki" : "≈örednie warunki"}</div>
            <div>üß∫ Pranie: {current.humidity < 60 ? "Idealnie na suszenie" : "Lepiej w domu"}</div>
            <div>üï∂Ô∏è Okulary: {current.uv > 3 ? "Zabierz okulary" : "Nie sƒÖ potrzebne"}</div>
        </div>
      </div>
    </div>
  );
};

export default WeatherHighlights;
