import React from 'react';
import Snowfall from 'react-snowfall';

// Import obrazków
import springBg from '../shared/art/spring.webp';
import summerBg from '../shared/art/summer.webp'; // Sprawdź czy to .wbp czy .webp
import autumnBg from '../shared/art/autumn.jpg';
import winterBg from '../shared/art/snow.jpg';

const WeatherBackground = ({ weatherData }) => {
  // 1. Obsługa ładowania (zanim przyjdą dane)
  if (!weatherData || !weatherData.current) {
    // Domyślne tło (np. wiosna)
    return <img src={springBg} alt="loading-bg" className="bg-video" />;
  }

  const code = weatherData.current.condition.code;
  const isDay = weatherData.current.is_day; // 1 = dzień, 0 = noc
  const month = new Date().getMonth() + 1; // 1-12

  // --- LOGIKA WYBORU TŁA ---
  
  let backgroundSrc = springBg;
  let showSnow = false;
  let overlayColor = 'rgba(0, 0, 0, 0.2)'; // Lekkie przyciemnienie

  // Kody pogody WeatherAPI
  // Śnieg (kody: 1066, 1114, 1117, 1210-1225, etc.)
  const snowCodes = [1066, 1069, 1072, 1114, 1117, 1210, 1213, 1216, 1219, 1222, 1225, 1237, 1255, 1258, 1261, 1264];
  
  // Deszcz (kody: 1063, 1180-1201, 1240-1252)
  const rainCodes = [1063, 1180, 1183, 1186, 1189, 1192, 1195, 1198, 1201, 1240, 1243, 1246, 1273, 1276];

  // 2. Sprawdzamy warunki
  if (snowCodes.includes(code)) {
    backgroundSrc = winterBg;
    showSnow = true; // Włączamy bibliotekę Snowfall
    overlayColor = 'rgba(0, 0, 0, 0.1)'; // Jaśniej dla śniegu
  } 
  else if (rainCodes.includes(code)) {
    // Jeśli pada deszcz, bierzemy tło pory roku, ale mocno przyciemniamy
    // (Chyba że masz osobny obrazek 'rain.jpg')
    backgroundSrc = getSeasonalBackground(month);
    overlayColor = 'rgba(0, 0, 30, 0.5)'; // Ciemny granatowy filtr
  } 
  else {
    // Brak ekstremalnej pogody -> użyj tła pory roku
    backgroundSrc = getSeasonalBackground(month);
    
    // Jeśli jest noc i bezchmurnie, przyciemniamy mocniej
    if (isDay === 0) {
      overlayColor = 'rgba(0, 0, 10, 0.7)';
    }
  }

  return (
    <>
      {/* Główne zdjęcie tła */}
      <img 
        src={backgroundSrc} 
        alt="weather-background" 
        className="bg-video" 
        style={{ 
           filter: `brightness(${isDay === 0 ? 0.5 : 0.9})` // Dodatkowe przyciemnienie w nocy przez CSS
        }} 
      />
      
      {/* Warstwa koloru (filtr) */}
      <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100dvh',
          backgroundColor: overlayColor, zIndex: -1, pointerEvents: 'none',
          transition: 'background-color 1s ease'
      }} />
      
      {/* Efekt śniegu */}
      {showSnow && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100dvh', zIndex: 0, pointerEvents: 'none' }}>
           <Snowfall color="white" snowflakeCount={200} />
        </div>
      )}
    </>
  );
};

// Funkcja pomocnicza do pór roku
const getSeasonalBackground = (month) => {
  // Zima: Grudzień, Styczeń, Luty
  if (month === 12 || month === 1 || month === 2) return winterBg;
  // Wiosna: Marzec, Kwiecień, Maj
  if (month >= 3 && month <= 5) return springBg;
  // Lato: Czerwiec, Lipiec, Sierpień
  if (month >= 6 && month <= 8) return summerBg;
  // Jesień: Wrzesień, Październik, Listopad
  return autumnBg;
};

export default WeatherBackground;
