import React, { useState, useEffect } from 'react';
import bgGif from './shared/art/snowFall-animated.цуиз';
import './shared/styles/style.css';
import Header from './widgets/header';
import Footer from './widgets/Footer';
import UpWeatherPc from './widgets/UpWeatherPc';
import WeatherManager from './widgets/WeatherManager';
import WeatherHighlights from './widgets/WeatherHighlights';
import { fetchWeatherData } from './widgets/WeatherApi';

function App() {
  const [searchedCity, setSearchedCity] = useState('Lublin');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleCitySearch = (city) => {
    setSearchedCity(city);
  };

  useEffect(() => {
    setLoading(true);
    fetchWeatherData(searchedCity)
      .then(data => { setWeather(data); setLoading(false); })
      .catch(() => setLoading(false));
  }, [searchedCity]);
  return (
    <div className="app-container">
      <img src={bgGif} alt="background" className="bg-video" />

          <Header onSearch={handleCitySearch}/>
          <UpWeatherPc Scity={searchedCity} />
          <WeatherManager city={searchedCity} />
          <WeatherHighlights data={weather?.fullRawData} />
          {/* <UpWetherMobile/>

          <DownWeatherPc/>
          <DownWeatherMobile/> */}
          <Footer />



    </div>
  );
}

export default App;
