import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, Label 
} from 'recharts';

const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 3 !== 0) return null;
  const iconSize = 40; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 10} width={iconSize} height={iconSize} viewBox="0 0 64 64">
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

const WeatherChart = ({ data, sunrise, sunset }) => {
  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–¥–≥–æ–Ω–∞ –≤—Ä–µ–º–µ–Ω–∏ AM/PM –ø–æ–¥ —Ñ–æ—Ä–º–∞—Ç –æ—Å–∏ X (00:00)
  const formatAstroTime = (timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    return `${hours.toString().padStart(2, '0')}:00`; // –û–∫—Ä—É–≥–ª—è–µ–º –¥–æ —á–∞—Å–∞ –¥–ª—è –ø–æ–ø–∞–¥–∞–Ω–∏—è –≤ —Ç–æ—á–∫—É
  };

  const sunRiseX = formatAstroTime(sunrise);
  const sunSetX = formatAstroTime(sunset);

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '350px', width: '100%', marginTop: '20px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis dataKey="time" stroke="rgba(255,255,255,0.5)" fontSize={12} tickLine={false} axisLine={false} />
          <YAxis hide domain={['dataMin - 3', 'dataMax + 3']} />
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          {/* –õ–∏–Ω–∏—è –†–∞—Å—Å–≤–µ—Ç–∞ */}
          {sunRiseX && (
            <ReferenceLine x={sunRiseX} stroke="#ffc658" strokeDasharray="5 5">
              <Label value="üåÖ Sunrise" position="top" fill="#ffc658" fontSize={12} offset={10} />
            </ReferenceLine>
          )}

          {/* –õ–∏–Ω–∏—è –ó–∞–∫–∞—Ç–∞ */}
          {sunSetX && (
            <ReferenceLine x={sunSetX} stroke="#ff7300" strokeDasharray="5 5">
              <Label value="üåá Sunset" position="top" fill="#ff7300" fontSize={12} offset={10} />
            </ReferenceLine>
          )}

          <Tooltip contentStyle={{ backgroundColor: 'rgba(0,0,0,0.8)', border: 'none', borderRadius: '10px', color: '#fff' }} />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={3} 
            fillOpacity={1} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot />} 
            isAnimationActive={false} 
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const getWeatherData = async () => {
      const cleanCity = city.trim();
      if (!cleanCity) return;

      try {
        setLoading(true);
        setError(null);
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(cleanCity)}&days=1`
        );
        
        if (!response.ok) throw new Error('Location not found');
        const json = await response.json();
        
        setAstro({
          sunrise: json.forecast.forecastday[0].astro.sunrise,
          sunset: json.forecast.forecastday[0].astro.sunset
        });

        const formattedData = json.forecast.forecastday[0].hour.map((h) => ({
          time: h.time.split(' ')[1], 
          temp: h.temp_c,
          icon: h.condition.icon
        }));

        setChartData(formattedData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    getWeatherData();
  }, [city]);

  if (loading) return <div style={{ color: '#fff', padding: '20px' }}>Loading...</div>;
  if (error) return <div style={{ color: '#ff4d4d', padding: '20px' }}>Error: {error}</div>;

  return (
    <div className="weather-manager" style={{ padding: '20px' }}>
      <h2 style={{ color: '#fff', fontWeight: '300', margin: 0 }}>{city}</h2>
      <WeatherChart data={chartData} sunrise={astro.sunrise} sunset={astro.sunset} />
    </div>
  );
};

export default WeatherManager;
