import React, { useState, useEffect } from 'react';

function UserManager() {
  // Состояния для списка и для полей ввода
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [city, setCity] = useState('');

  // 1. ЗАГРУЗКА ПРИ СТАРТЕ
  useEffect(() => {
    const saved = localStorage.getItem('app_users');
    if (saved) {
      // Компьютер превращает строку из LocalStorage обратно в массив объектов
      setUsers(JSON.parse(saved));
    }
  }, []);

  // 2. ФУНКЦИЯ ДОБАВЛЕНИЯ
  const handleAddUser = (e) => {
    e.preventDefault(); // Чтобы страница не перезагружалась при отправке формы
    
    if (!name.trim() || !city.trim()) return; // Проверка на пустые поля

    const newUser = {
      // Генерируем уникальный "бронированный" ID на уровне браузера
      id: crypto.randomUUID(), 
      name: name,
      city: city
    };

    const newUsersList = [...users, newUser];
    
    // Обновляем и экран (state), и память браузера (localStorage)
    setUsers(newUsersList);
    localStorage.setItem('app_users', JSON.stringify(newUsersList));

    // Очищаем инпуты
    setName('');
    setCity('');
  };

  // 3. ФУНКЦИЯ УДАЛЕНИЯ
  const handleDelete = (id) => {
    const filteredUsers = users.filter(user => user.id !== id);
    setUsers(filteredUsers);
    localStorage.setItem('app_users', JSON.stringify(filteredUsers));
  };

  return (
    <div className="glass-panel" style={{ color: 'white', padding: '2rem' }}>
      <h2>Новый профиль</h2>
      
      <form onSubmit={handleAddUser} style={{ marginBottom: '20px' }}>
        <input 
          type="text"
          placeholder="Ваше имя"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="my-input"
        />
        <input 
          type="text"
          placeholder="Ваш город"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="my-input"
        />
        <button type="submit">Запомнить меня</button>
      </form>

      <div className="user-list">
        <h3>Зарегистрированные пользователи:</h3>
        {users.length === 0 ? (
          <p>Пока никого нет...</p>
        ) : (
          users.map((user) => (
            <div key={user.id} className="user-card" style={{ borderBottom: '1px solid rgba(255,255,255,0.2)', padding: '10px 0' }}>
              <span><strong>{user.name}</strong> из г. {user.city}</span>
              <button 
                onClick={() => handleDelete(user.id)} 
                style={{ marginLeft: '15px', color: 'red', cursor: 'pointer' }}
              >
                Удалить
              </button>
              <br />
              <small style={{ opacity: 0.5 }}>ID: {user.id}</small>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default UserManager;
