import React, { useState, useEffect } from 'react';
import { fetchWeatherData } from './weatherApi';
import WeatherChart from './WeatherChart';
import WeatherHighlights from './WeatherHighlights';

const WeatherManager = ({ city = 'London' }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchWeatherData(city)
      .then(res => { setData(res); setLoading(false); })
      .catch(() => setLoading(false));
  }, [city]);

  if (loading) return <div style={{color: '#fff'}}>Wczytywanie...</div>;
  if (!data) return <div style={{color: '#f00'}}>Błąd pobierania danych</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1000px', margin: '0 auto' }}>
      <h1 style={{ color: '#fff' }}>{data.locationName}</h1>
      <WeatherChart data={data.chartData} sunrise={data.astro.sunrise} sunset={data.astro.sunset} />
      <WeatherHighlights data={data.fullRawData} />
    </div>
  );
};

export default WeatherManager;
