import React, { useState, useEffect } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 3 !== 0) return null;
  const iconSize = 50; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 10} width={iconSize} height={iconSize} viewBox="0 0 64 64">
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

const WeatherChart = ({ data }) => {
  return (
    <div className="chart-wrapper glass-panel" style={{ height: '300px', width: '100%' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          <XAxis dataKey="time" stroke="rgba(255,255,255,0.5)" fontSize={12} tickLine={false} axisLine={false} />
          <YAxis hide domain={['dataMin - 5', 'dataMax + 5']} />
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          <Tooltip contentStyle={{ backgroundColor: 'rgba(0,0,0,0.8)', border: 'none', borderRadius: '10px', color: '#fff' }} />
          <Area type="monotone" dataKey="temp" stroke="#8884d8" strokeWidth={3} fillOpacity={1} fill="url(#colorTemp)" dot={<CustomizedDot />} isAnimationActive={false} />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Lublin' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' }); // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Å–æ–ª–Ω—Ü–∞
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const getWeatherData = async () => {
      // –û—á–∏—â–∞–µ–º –ø—Ä–æ–±–µ–ª—ã –ø–æ –∫—Ä–∞—è–º
      const searchCity = city.trim();
      
      if (!searchCity) return;

      try {
        setLoading(true);
        setError(null);

        // –ò—Å–ø–æ–ª—å–∑—É–µ–º encodeURIComponent –¥–ª—è –±–µ–∑–æ–ø–∞—Å–Ω–æ–π –ø–µ—Ä–µ–¥–∞—á–∏ –Ω–∞–∑–≤–∞–Ω–∏—è –≥–æ—Ä–æ–¥–∞
        const url = `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(searchCity)}&days=1&aqi=no&alerts=no`;
        
        const response = await fetch(url);
        
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error.message || 'City not found');
        }
        
        const json = await response.json();
        
        setAstro({
          sunrise: json.forecast.forecastday[0].astro.sunrise,
          sunset: json.forecast.forecastday[0].astro.sunset
        });

        const formattedData = json.forecast.forecastday[0].hour.map((h) => ({
          time: h.time.split(' ')[1], 
          temp: h.temp_c,
          icon: h.condition.icon,
          condition: h.condition.text
        }));

        setChartData(formattedData);
        setLoading(false);
      } catch (err) {
        console.error("Weather API Error:", err.message);
        setError(err.message);
        setLoading(false);
      }
    };

    getWeatherData();
  }, [city]);

  if (loading) return <div style={{ color: '#fff', padding: '20px' }}>Loading...</div>;
  if (error) return <div style={{ color: '#ff4d4d', padding: '20px' }}>Error: {error}</div>;

  return (
    <div className="weather-manager" style={{ padding: '20px', color: '#fff' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end', marginBottom: '10px' }}>
        <div>
          <h2 style={{ margin: 0, fontWeight: '300' }}>Next 24 hours: {city}</h2>
        </div>
        
        {/* –ü–∞–Ω–µ–ª—å —Ä–∞—Å—Å–≤–µ—Ç–∞ –∏ –∑–∞–∫–∞—Ç–∞ */}
        <div style={{ display: 'flex', gap: '20px', fontSize: '14px', opacity: 0.8 }}>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '18px' }}>üåÖ</span>
            <span>{astro.sunrise}</span>
          </div>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '18px' }}>üåá</span>
            <span>{astro.sunset}</span>
          </div>
        </div>
      </div>

      <WeatherChart data={chartData} />
    </div>
  );
};

export default WeatherManager;
