import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, ReferenceDot, Label 
} from 'recharts';

const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 6 !== 0) return null; // Иконки реже, чтобы не мешать текущему времени
  const iconSize = 34; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 10} width={iconSize} height={iconSize}>
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

const WeatherChart = ({ data, sunrise, sunset }) => {
  // 1. Получаем текущее время в формате "HH:00"
  const now = new Date();
  const currentHourX = `${now.getHours().toString().padStart(2, '0')}:00`;

  // Находим данные для точки "Сейчас" (температура в текущий час)
  const currentDataPoint = data.find(d => d.time === currentHourX);

  const to24h = (timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    return `${hours.toString().padStart(2, '0')}:00`;
  };

  const sunX1 = to24h(sunrise);
  const sunX2 = to24h(sunset);

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '350px', width: '100%', padding: '10px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis dataKey="time" stroke="#888" fontSize={12} tickLine={false} axisLine={false} />
          <YAxis hide domain={['dataMin - 3', 'dataMax + 3']} />
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.05)" />
          
          {/* Линии рассвета и заката */}
          {sunX1 && <ReferenceLine x={sunX1} stroke="#ffd700" strokeDasharray="3 3" />}
          {sunX2 && <ReferenceLine x={sunX2} stroke="#ff4500" strokeDasharray="3 3" />}

          {/* --- МЕТКА "СЕЙЧАС" --- */}
          {currentDataPoint && (
            <>
              <ReferenceLine x={currentHourX} stroke="#00f2ff" strokeWidth={2} strokeDasharray="5 5">
                <Label value="СЕЙЧАС" position="top" fill="#00f2ff" fontSize={10} fontWeight="bold" offset={15} />
              </ReferenceLine>
              <ReferenceDot 
                x={currentHourX} 
                y={currentDataPoint.temp} 
                r={6} 
                fill="#00f2ff" 
                stroke="#fff" 
                strokeWidth={2} 
              />
            </>
          )}

          <Tooltip 
            contentStyle={{ backgroundColor: '#1a1a1a', border: 'none', borderRadius: '10px' }} 
            itemStyle={{ color: '#fff' }}
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={4} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot />}
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        setError(null);

        // Попытка найти город (добавили замену для часто неверно написанных городов)
        let query = city.trim();
        if (query.toLowerCase() === 'vinnytsia') query = 'Vinnitsa'; // Корректное имя для базы WeatherAPI

        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=1`
        );
        
        if (!response.ok) throw new Error('Город не найден');
        
        const json = await response.json();
        setAstro(json.forecast.forecastday[0].astro);
        
        const formatted = json.forecast.forecastday[0].hour.map(h => ({
          time: h.time.split(' ')[1],
          temp: h.temp_c,
          icon: h.condition.icon
        }));

        setChartData(formatted);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, [city]);

  if (loading) return <div className="glass-panel" style={{padding: '20px', color: '#fff'}}>Загрузка...</div>;
  if (error) return <div className="glass-panel" style={{padding: '20px', color: '#ff4d4d'}}>Ошибка: {error}</div>;

  return (
    <div className="weather-container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 15px', color: '#fff' }}>
         <h2 style={{ fontWeight: '200' }}>{city}</h2>
         <div style={{ textAlign: 'right', fontSize: '12px', opacity: 0.7 }}>
            Рассвет: {astro.sunrise} <br/>
            Закат: {astro.sunset}
         </div>
      </div>
      <WeatherChart data={chartData} sunrise={astro.sunrise} sunset={astro.sunset} />
    </div>
  );
};

export default WeatherManager;
