import React, { useState, useEffect } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
// Убедись, что путь к стилям верный! Если файла нет, закомментируй строку ниже
// import '../shared/styles/style.css'; 

// --- 1. Компонент КРУПНОЙ иконки ---
const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;

  // Показываем иконку каждые 3 часа
  if (index % 3 !== 0) return null;

  const iconSize = 50; 

  return (
    <svg 
      x={cx - iconSize / 2} 
      y={cy - iconSize - 10} 
      width={iconSize} 
      height={iconSize} 
      viewBox="0 0 64 64"
    >
      <image 
        href={`https:${payload.icon}`} 
        width="100%" 
        height="100%" 
      />
    </svg>
  );
};

// --- 2. Компонент Графика ---
const WeatherChart = ({ data }) => {
  return (
    <div className="chart-wrapper glass-panel" style={{ height: '300px', width: '100%' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis 
            dataKey="time" 
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false}
            axisLine={false}
          />
          <YAxis hide domain={['dataMin - 5', 'dataMax + 5']} />
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          <Tooltip 
            contentStyle={{ backgroundColor: 'rgba(0,0,0,0.8)', border: 'none', borderRadius: '10px', color: '#fff' }}
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={3}
            fillOpacity={1} 
            fill="url(#colorTemp)" 
            name="Temp"
            dot={<CustomizedDot />} 
            isAnimationActive={false} 
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

// --- 3. Главный компонент ---
const WeatherManager = ({ city = 'Lublin' }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const getWeatherData = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${city}&days=1&aqi=no&alerts=no`
        );
        if (!response.ok) throw new Error('City not found');
        
        const json = await response.json();
        
        const formattedData = json.forecast.forecastday[0].hour.map((h) => ({
          time: h.time.split(' ')[1], 
          temp: h.temp_c,
          icon: h.condition.icon,
          condition: h.condition.text
        }));

        setChartData(formattedData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    getWeatherData();
  }, [city]);

  if (loading) return <div style={{ color: '#fff' }}>Loading chart...</div>;
  if (error) return <div style={{ color: '#ff4d4d' }}>Error: {error}</div>;

  return (
    <div className="weather-manager">
      <h2 style={{ color: '#fff', fontWeight: '300' }}>Next 24 hours: {city}</h2>
      <WeatherChart data={chartData} />
    </div>
  );
};

// САМАЯ ВАЖНАЯ СТРОКА, КОТОРОЙ НЕ ХВАТАЛО:
export default WeatherManager;
