import React, { useState } from 'react';
import Header from './components/Header/Header';
import UserProfile from './components/UserProfile/UserProfile';
import WeatherStats from './components/WeatherStats/WeatherStats';
import MainChart from './components/MainChart/MainChart';
import FriendsSidebar from './components/FriendsSidebar/FriendsSidebar';
import './styles/main.scss';

function App() {
  // Stan na pogodę i listę znajomych
  const [weatherData, setWeatherData] = useState(null);
  const [friends, setFriends] = useState([]); // Twoja lista z imionami i lokalizacją

  return (
    <div className="app-container">
      {/* 1. TŁO WIDEO (Dynamiczne) */}
      <video className="bg-video" autoPlay loop muted>
        <source src="/assets/default-bg.mp4" type="video/mp4" />
      </video>

      {/* 2. UKŁAD STRONY (CSS Grid lub Flexbox) */}
      <div className="dashboard-grid">

        <header className="area-header">
          <Header quote="Cytat dnia: Nie ma złej pogody, jest tylko złe ubranie" />
        </header>

        <aside className="area-sidebar-left">
          <UserProfile name="Twoje Imię" avatar="/path/to/img" />
          <WeatherStats data={weatherData} />
        </aside>

        <main className="area-chart">
          <MainChart />
        </main>

        {/* 3. TWÓJ POMYSŁ: WYSUWANY PANEL ZNAJOMYCH */}
        <FriendsSidebar friends={friends} onAddFriend={(newFriend) => setFriends([...friends, newFriend])} />

      </div>
    </div>
  );
}

export default App;
