import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, ReferenceDot, Label 
} from 'recharts';

// Иконки погоды (теперь каждые 4 часа, чтобы не перегружать 48-часовой график)
const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 4 !== 0) return null; 
  const iconSize = 50; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 15} width={iconSize} height={iconSize}>
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

const SunIcon = ({ x, y, type }) => {
  const iconSize = 30;
  const iconUrl = type === 'sunrise' 
    ? "https://cdn.weatherapi.com/weather/64x64/day/113.png" 
    : "https://cdn.weatherapi.com/weather/64x64/night/113.png";
  return (
    <svg x={x - iconSize / 2} y={y} width={iconSize} height={iconSize}>
      <image href={iconUrl} width="100%" height="100%" />
    </svg>
  );
};

const WeatherChart = ({ data, astroToday, astroTomorrow }) => {
  const now = new Date();
  // Форматируем текущее время так же, как в данных (YYYY-MM-DD HH:00)
  const currentKey = `${now.getFullYear()}-${(now.getMonth()+1).toString().padStart(2,'0')}-${now.getDate().toString().padStart(2,'0')} ${now.getHours().toString().padStart(2, '0')}:00`;
  const currentDataPoint = data.find(d => d.fullTime === currentKey);

  // Функция для поиска "ключа" времени (рассвет/закат) в массиве из 48 часов
  const getAstroKey = (dateStr, timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    return `${dateStr} ${hours.toString().padStart(2, '0')}:00`;
  };

  // Ключи для линий (сегодня и завтра)
  const sunLines = [
    { key: getAstroKey(data[0]?.date, astroToday.sunrise), type: 'sunrise', label: 'Sun' },
    { key: getAstroKey(data[0]?.date, astroToday.sunset), type: 'sunset', label: 'Moon' },
    { key: getAstroKey(data[24]?.date, astroTomorrow.sunrise), type: 'sunrise', label: 'Sun' },
    { key: getAstroKey(data[24]?.date, astroTomorrow.sunset), type: 'sunset', label: 'Moon' }
  ];

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '450px', width: '100%', padding: '20px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 80, right: 20, left: -20, bottom: 20 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0.1}/>
            </linearGradient>
          </defs>
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.05)" />
          
          <XAxis 
            dataKey="fullTime" 
            tickFormatter={(str) => str.split(' ')[1]} // Показываем только "14:00"
            interval={5} // Показываем метку каждые 6 часов (0, 6, 12...)
            stroke="rgba(255,255,255,0.5)" 
            fontSize={11}
            tickLine={false}
            axisLine={false}
          />
          
          <YAxis hide domain={['dataMin - 5', 'dataMax + 5']} />
          
          {/* Отрисовка линий рассвета/заката на 48 часов */}
          {sunLines.map((sun, index) => sun.key && (
            <ReferenceLine key={index} x={sun.key} stroke="rgba(255,255,255,0.2)" strokeDasharray="3 3">
               <Label content={<SunIcon type={sun.type} y={-5} />} />
            </ReferenceLine>
          ))}

          {/* Метка СЕЙЧАС */}
          {currentDataPoint && (
            <ReferenceLine x={currentKey} stroke="#00f2ff" strokeWidth={2}>
              <Label value="NOW" position="top" fill="#00f2ff" fontSize={10} fontWeight="bold" offset={25} />
            </ReferenceLine>
          )}

          <Tooltip 
            labelFormatter={(value) => `Time: ${value}`}
            contentStyle={{ backgroundColor: '#1a1a1a', border: 'none', borderRadius: '10px' }} 
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={4} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot />}
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ today: {}, tomorrow: {} });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        let query = city.trim().toLowerCase() === 'vinnytsia' ? 'Vinnitsa' : city.trim();
        
        // ЗАПРОС: days=2
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=2`
        );
        if (!response.ok) throw new Error('City not found');
        const json = await response.json();
        
        setAstro({
          today: json.forecast.forecastday[0].astro,
          tomorrow: json.forecast.forecastday[1].astro
        });

        // СЛИЯНИЕ: Берем часы из обоих дней
        const day1 = json.forecast.forecastday[0].hour;
        const day2 = json.forecast.forecastday[1].hour;
        const combinedHours = [...day1, ...day2];

        const formatted = combinedHours.map(h => ({
          fullTime: h.time, // Используем полную строку "2024-05-10 14:00" как уникальный ID
          date: h.time.split(' ')[0],
          time: h.time.split(' ')[1],
          temp: h.temp_c,
          icon: h.condition.icon
        }));

        setChartData(formatted);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchWeather();
  }, [city]);

  if (loading) return <div className="glass-panel" style={{color: '#fff', padding: '20px'}}>Loading 48h forecast...</div>;

  return (
    <div className="weather-container" style={{ padding: '20px' }}>
      <h1 style={{ color: '#fff', fontWeight: '200' }}>{city} (48 Hours)</h1>
      <WeatherChart 
        data={chartData} 
        astroToday={astro.today} 
        astroTomorrow={astro.tomorrow} 
      />
    </div>
  );
};

export default WeatherManager;
