import React from 'react';












const WeatherHighlights = ({ data }) => {
  if (!data || !data.current) return null;
  const { current, forecast } = data;

  const getAirQualityLabel = (index) => {
    const levels = {
      1: { text: 'Świetna', color: '#4ade80' },
      2: { text: 'Dobra', color: '#a3e635' },
      3: { text: 'Średnia', color: '#facc15' },
      4: { text: 'Zła', color: '#fb923c' },
      5: { text: 'Bardzo zła', color: '#f87171' },
      6: { text: 'Hazardowa', color: '#b91c1c' }
    };
    return levels[index] || { text: 'Brak danych', color: '#fff' };
  };

  const aqi = getAirQualityLabel(current.air_quality?.['us-epa-index']);



  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
      {/* Wiatr */}
      <div className="card-glass" style={cardStyle}>
        <span style={titleStyle}>Wiatr</span>
        <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{current.wind_kph} km/h</div>
        <div style={{ transform: `rotate(${current.wind_degree}deg)`, width: '30px' }}>⬆️</div>
      </div>
      {/* AQI & Wilgotność */}
      <div className="card-glass" style={cardStyle}>
        <span style={titleStyle}>Jakość Powietrza</span>
        <div style={{ color: aqi.color, fontSize: '22px', fontWeight: 'bold' }}>{aqi.text}</div>
        <div style={titleStyle}>Wilgotność: {current.humidity}%</div>
      </div>
      {/* Lifestyle */}
      
    </div>
  );
};

const cardStyle = { background: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '15px', color: '#fff' };
const titleStyle = { fontSize: '12px', opacity: 0.6, textTransform: 'uppercase' };

export default WeatherHighlights;
