import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, Label 
} from 'recharts';

const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 4 !== 0) return null; // –†–µ–∂–µ –∏–∫–æ–Ω–∫–∏, —á—Ç–æ–±—ã –Ω–µ –º–µ—à–∞–ª–∏ –ª–∏–Ω–∏—è–º
  const iconSize = 40; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 10} width={iconSize} height={iconSize}>
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

const WeatherChart = ({ data, sunrise, sunset }) => {
  // –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–≤–æ–¥–∞ 12h (AM/PM) –≤ 24h –¥–ª—è –æ—Å–∏ X
  const to24h = (timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    return `${hours.toString().padStart(2, '0')}:00`;
  };

  const sunX1 = to24h(sunrise);
  const sunX2 = to24h(sunset);

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '350px', width: '100%', padding: '10px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis dataKey="time" stroke="#888" fontSize={12} tickLine={false} axisLine={false} />
          <YAxis hide domain={['dataMin - 3', 'dataMax + 3']} />
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.05)" />
          
          {/* –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–∞—è –ª–∏–Ω–∏—è –†–∞—Å—Å–≤–µ—Ç–∞ */}
          {sunX1 && (
            <ReferenceLine x={sunX1} stroke="#ffd700" strokeDasharray="3 3" strokeWidth={2}>
              <Label value="üåÖ" position="top" offset={10} />
            </ReferenceLine>
          )}

          {/* –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω–∞—è –ª–∏–Ω–∏—è –ó–∞–∫–∞—Ç–∞ */}
          {sunX2 && (
            <ReferenceLine x={sunX2} stroke="#ff4500" strokeDasharray="3 3" strokeWidth={2}>
              <Label value="üåá" position="top" offset={10} />
            </ReferenceLine>
          )}

          <Tooltip 
            contentStyle={{ backgroundColor: '#1a1a1a', border: 'none', borderRadius: '10px' }} 
            itemStyle={{ color: '#fff' }}
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={4} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot />}
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        setError(null);

        // –ü–æ–ø—ã—Ç–∫–∞ 1: –ü—Ä–æ—Å—Ç–æ –≥–æ—Ä–æ–¥. –ü–æ–ø—ã—Ç–∫–∞ 2: –ì–æ—Ä–æ–¥ + –£–∫—Ä–∞–∏–Ω–∞
        let query = city.trim();
        let response = await fetch(`https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=1`);
        
        if (!response.ok) {
           query = `${city.trim()},Ukraine`;
           response = await fetch(`https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=1`);
        }

        if (!response.ok) throw new Error('Location not found in database');
        
        const json = await response.json();
        
        setAstro(json.forecast.forecastday[0].astro);
        
        const formatted = json.forecast.forecastday[0].hour.map(h => ({
          time: h.time.split(' ')[1],
          temp: h.temp_c,
          icon: h.condition.icon
        }));

        setChartData(formatted);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, [city]);

  if (loading) return <div className="glass-panel" style={{padding: '20px', color: '#fff'}}>Loading...</div>;
  if (error) return <div className="glass-panel" style={{padding: '20px', color: '#ff4d4d'}}>Error: {error}</div>;

  return (
    <div className="weather-container">
      <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0 10px' }}>
         <h2 style={{ color: '#fff', fontWeight: '200' }}>{city}</h2>
         <div style={{ color: '#ffd700', fontSize: '14px' }}>
            {astro.sunrise} ‚Äî {astro.sunset}
         </div>
      </div>
      <WeatherChart data={chartData} sunrise={astro.sunrise} sunset={astro.sunset} />
    </div>
  );
};

export default WeatherManager;
