import React, { useState } from 'react';
import '../shared/styles/style.css'; // Upewnij siƒô, ≈ºe tu sƒÖ nowe style

const Header = ({ onSearch }) => {
  const [langOpen, setLangOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      if (inputValue.trim()) {
        onSearch(inputValue);
        setInputValue('');
        setLangOpen(false); // Zamykamy menu przy szukaniu
      }
    }
  };

  return (
    <header className="header glass-panel">
      <div className='logo-language-div'>
        <div className="logo">WeatherApp</div>
      </div>

      <div className="search-bar">
        <input 
          type="text" 
          placeholder="Search city..." 
          className="my-input"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
        />
      </div>

      <div className="nav-buttons">
        <div className="dropdown-container">
          <button onClick={() => setLangOpen(!langOpen)} className="nav-btn">
             üåê Jƒôzyk
          </button>
          
          <div className={`lang-menu ${langOpen ? 'active' : ''}`}>
             {[
               { code: 'gb', name: 'English' },
               { code: 'pl', name: 'Polski' },
               { code: 'ua', name: '–£–∫—Ä–∞—ó–Ω—Å—å–∫–∞' },
               { code: 'es', name: 'Espa√±ol' },
               { code: 'fr', name: 'Fran√ßais' }
             ].map((lang) => (
               <button key={lang.code} className="lang-item" onClick={() => setLangOpen(false)}>
                 <img src={`https://flagcdn.com/w20/${lang.code}.png`} alt={lang.name} className="flag-img" />
                 <span className="lang-name">{lang.name}</span>
               </button>
             ))}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
