import React from 'react';
import '../shared/styles/style.css';

const WeatherHighlights = ({ data }) => {
  if (!data || !data.current) return null;
  const { current } = data;

  const getAirQualityLabel = (index) => {
    const levels = {
      1: { text: 'Świetna', color: '#4ade80' },
      2: { text: 'Dobra', color: '#a3e635' },
      3: { text: 'Średnia', color: '#facc15' },
      4: { text: 'Zła', color: '#fb923c' },
      5: { text: 'Bardzo zła', color: '#f87171' },
      6: { text: 'Hazardowa', color: '#b91c1c' }
    };
    return levels[index] || { text: 'Brak danych', color: '#fff' };
  };

  const aqi = getAirQualityLabel(current.air_quality?.['us-epa-index']);

  return (
    <div className="highlights-grid">
      {/* Wiatr */}
      <div className="highlight-card">
        <span className="highlight-title">Wiatr</span>
        <div className="highlight-value">
          {current.wind_kph} <small>km/h</small>
        </div>
        <div className="highlight-footer">
          <span 
            className="wind-arrow" 
            style={{ transform: `rotate(${current.wind_degree}deg)` }}
          >
            ⬆️
          </span>
          <span>{current.wind_dir}</span>
        </div>
      </div>

      {/* AQI */}
      <div className="highlight-card">
        <span className="highlight-title">Jakość Powietrza</span>
        <div className="highlight-value" style={{ color: aqi.color }}>
          {aqi.text}
        </div>
        <div className="highlight-footer">
          Indeks EPA: {current.air_quality?.['us-epa-index'] || 'N/A'}
        </div>
      </div>

      {/* Wilgotność */}
      <div className="highlight-card">
        <span className="highlight-title">Wilgotność</span>
        <div className="highlight-value">{current.humidity}%</div>
        <div className="highlight-footer">
          Punkt rosy: {current.dewpoint_c}°C
        </div>
      </div>
    </div>
  );
};

export default WeatherHighlights;
