import React from 'react';

import '../shared/styles/style.css';



const WeatherHighlights = ({ data }) => {
  if (!data || !data.current) return null;
  const { current } = data;

  const getAirQualityLabel = (index) => {
    const levels = {
      1: { text: 'Świetna', color: '#4ade80' },
      2: { text: 'Dobra', color: '#a3e635' },
      3: { text: 'Średnia', color: '#facc15' },
      4: { text: 'Zła', color: '#fb923c' },
      5: { text: 'Bardzo zła', color: '#f87171' },
      6: { text: 'Hazardowa', color: '#b91c1c' }
    };
    return levels[index] || { text: 'Brak danych', color: '#fff' };
  };

  const aqi = getAirQualityLabel(current.air_quality?.['us-epa-index']);

  return (
    <div style={styles.grid}>
      {/* Karta: Wiatr */}
      <div style={styles.card}>
        <span style={styles.title}>Wiatr</span>
        <div style={styles.value}>{current.wind_kph} <small style={{fontSize: '14px'}}>km/h</small></div>
        <div style={styles.footer}>
          <div style={{ transform: `rotate(${current.wind_degree}deg)`, transition: '0.5s' }}>⬆️</div>
          <span>Kierunek: {current.wind_dir}</span>
        </div>
      </div>

      {/* Karta: AQI */}
      <div style={styles.card}>
        <span style={styles.title}>Jakość Powietrza</span>
        <div style={{ ...styles.value, color: aqi.color }}>{aqi.text}</div>
        <div style={styles.footer}>
          Indeks EPA: {current.air_quality?.['us-epa-index'] || 'N/A'}
        </div>
      </div>

      {/* Karta: Wilgotność & Punkt Rosy */}
      <div style={styles.card}>
        <span style={styles.title}>Wilgotność</span>
        <div style={styles.value}>{current.humidity}%</div>
        <div style={styles.footer}>
          Punkt rosy: {current.dewpoint_c}°C
        </div>
      </div>
    </div>
  );
};

export default WeatherHighlights;
