import React from 'react';
import '../shared/styles/style.css';
import UserCard from '../features/UserCard';
import SearchLocation from '../features/SearchLocation';
import WeatherForecast from '../features/WeatherForecast';

const UpWeatherPc = () => {
  // Твои данные, которые придут из API
  const forecastFromApi = [
    { date: '2026-02-13', condition: 'cloudy', tempDay: 6, tempNight: 0 },
    { date: '2026-02-14', condition: 'cloudy', tempDay: 1, tempNight: -5 },
    { date: '2026-02-15', condition: 'snow', tempDay: -6, tempNight: -12 },
    { date: '2026-02-16', condition: 'cloudy', tempDay: -6, tempNight: -12 },
    { date: '2026-02-17', condition: 'cloudy_snow', tempDay: -2, tempNight: -7 },
    { date: '2026-02-18', condition: 'cloudy_snow', tempDay: -1, tempNight: -5 },
    { date: '2026-02-19', condition: 'cloudy', tempDay: -1, tempNight: -4 },
  ];

  return (
    <div className="up-weather-pc glass-panel">
      <UserCard 
        name="Pavel" 
        location="Lublin, Poland" 
        avatar="https://randomuser.me/api/portraits/men/1.jpg" 
      />

      <SearchLocation Searchcountry="Poland" Searchcity="Lublin" />

      
      <WeatherForecast data={forecastFromApi} />
    </div>
  );
}

export default UpWeatherPc;
