import React, { useState, useEffect } from 'react';
import '../shared/styles/style.css';
import UserCard from '../features/UserCard';
import SearchLocation from '../features/SearchLocation';
import WeatherForecast from '../features/WeatherForecast';



const UpWeatherPc = () => {
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);

  // Координаты для Люблина (можно потом сделать динамическими)
  const LAT = 51.25;
  const LON = 22.57;

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await fetch(
          `https://api.open-meteo.com/v1/forecast?latitude=${LAT}&longitude=${LON}&daily=weather_code,temperature_2m_max,temperature_2m_min&timezone=auto`
        );
        const data = await response.json();

        // Трансформируем данные API под твой формат
        const formattedData = data.daily.time.map((date, index) => ({
          date: date,
          // Превращаем WMO код погоды в твои строковые условия
          condition: mapWmoCodeToCondition(data.daily.weather_code[index]),
          tempDay: Math.round(data.daily.temperature_2m_max[index]),
          tempNight: Math.round(data.daily.temperature_2m_min[index]),
        }));

        setForecast(formattedData);
        setLoading(false);
      } catch (error) {
        console.error("Ошибка при загрузке погоды:", error);
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  // Вспомогательная функция для маппинга кодов Open-Meteo (WMO) в твои классы
  const mapWmoCodeToCondition = (code) => {
    if (code === 0) return 'sunny';
    if (code >= 1 && code <= 3) return 'cloudy';
    if (code >= 45 && code <= 48) return 'cloudy'; // Туман
    if (code >= 51 && code <= 67) return 'rain';
    if (code >= 71 && code <= 77) return 'snow';
    if (code >= 80 && code <= 82) return 'cloudy_rain';
    if (code >= 85 && code <= 86) return 'cloudy_snow';
    return 'cloudy'; // Значение по умолчанию
  };

  return (
    <div className="up-weather-pc">
      <UserCard 
        name="Pavel" 
        location="Lublin, Poland" 
        avatar="https://randomuser.me/api/portraits/men/1.jpg" 
      />

      <div className="location-search">
      <SearchLocation Searchcountry="Poland" Searchcity="Lublin" />

      {loading ? (
        <div className="loading">Загрузка прогноза...</div>
      ) : (
        <WeatherForecast data={forecast} />
      )}
    </div>
    </div>
  );
}

export default UpWeatherPc;
