import React from 'react';

// Proste style CSS wewnƒÖtrz pliku dla wygody (mo≈ºesz przenie≈õƒá do style.css)
const styles = {
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: '20px',
    marginTop: '20px',
    color: '#fff',
    fontFamily: 'sans-serif',
  },
  card: {
    background: 'rgba(255, 255, 255, 0.1)', // Efekt szk≈Ça
    backdropFilter: 'blur(10px)',
    borderRadius: '15px',
    padding: '20px',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
  },
  title: {
    fontSize: '14px',
    opacity: 0.7,
    marginBottom: '10px',
    textTransform: 'uppercase',
    letterSpacing: '1px',
  },
  value: {
    fontSize: '28px',
    fontWeight: 'bold',
  },
  row: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  tipItem: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    fontSize: '14px',
    marginBottom: '8px',
    padding: '8px',
    background: 'rgba(0,0,0,0.2)',
    borderRadius: '8px',
  }
};

const AdditionalInfo = ({ data }) => {
  // Zabezpieczenie przed brakiem danych
  if (!data || !data.current) return null;

  const { current, forecast } = data;
  
  // === LOGIKA DLA LIFESTYLE ===
  const getLifestyleTips = () => {
    const tips = [];
    
    // 1. Okulary (UV)
    if (current.uv > 3) {
      tips.push({ icon: 'üòé', text: 'Zabierz okulary przeciws≈Çoneczne' });
    } else {
      tips.push({ icon: 'üï∂Ô∏è', text: 'Okulary niepotrzebne (niskie UV)' });
    }

    // 2. Bieganie (Temp + Wiatr)
    // Zamieniamy mph na kph je≈õli trzeba, ale API zazwyczaj daje oba. U≈ºywam kph.
    const windKph = current.wind_kph;
    const temp = current.temp_c;
    const isRaining = current.precip_mm > 0.5;

    if (temp > 5 && temp < 25 && windKph < 25 && !isRaining) {
      tips.push({ icon: 'üèÉ', text: '≈öwietne warunki na bieganie!' });
    } else if (isRaining) {
      tips.push({ icon: 'üåßÔ∏è', text: 'Lepiej pobiegaj na bie≈ºni (pada)' });
    } else {
      tips.push({ icon: 'üè†', text: 'Warunki ≈õrednie na trening' });
    }

    // 3. Pranie (Wilgotno≈õƒá + Szansa na deszcz z prognozy na dzi≈õ)
    const humidity = current.humidity;
    const rainChance = forecast?.forecastday[0]?.day?.daily_chance_of_rain || 0;

    if (humidity < 60 && rainChance < 20) {
      tips.push({ icon: 'üëï', text: 'Idealna pogoda na suszenie prania' });
    } else {
      tips.push({ icon: 'üß∫', text: 'Lepiej susz pranie w domu' });
    }

    return tips;
  };

  // === LOGIKA JAKO≈öCI POWIETRZA ===
  const getAirQualityLabel = (index) => {
    // US EPA Index: 1-Good, 2-Moderate, 3-Unhealthy for sensitive, 4-Unhealthy, 5-Very Unhealthy, 6-Hazardous
    switch (index) {
      case 1: return { text: '≈öwietna', color: '#4ade80' }; // Zielony
      case 2: return { text: 'Dobra', color: '#a3e635' };
      case 3: return { text: '≈örednia', color: '#facc15' }; // ≈ª√≥≈Çty
      case 4: return { text: 'Z≈Ça', color: '#fb923c' }; // Pomara≈Ñczowy
      case 5: 
      case 6: return { text: 'Bardzo z≈Ça', color: '#f87171' }; // Czerwony
      default: return { text: 'Brak danych', color: '#fff' };
    }
  };

  const aqiIndex = current.air_quality ? current.air_quality['us-epa-index'] : 0;
  const aqiInfo = getAirQualityLabel(aqiIndex);

  return (
    <div style={styles.grid}>
      
      {/* 1. Karta Wiatru */}
      <div style={styles.card}>
        <div style={styles.title}>Wiatr</div>
        <div style={styles.row}>
          <div>
            <div style={styles.value}>{current.wind_kph} <span style={{fontSize: '16px'}}>km/h</span></div>
            <div style={{opacity: 0.6}}>Porywy: {current.gust_kph} km/h</div>
          </div>
          {/* Kompas */}
          <div style={{
            position: 'relative', width: '60px', height: '60px', 
            border: '2px solid rgba(255,255,255,0.3)', borderRadius: '50%',
            display: 'flex', alignItems: 'center', justifyContent: 'center'
          }}>
            <div style={{ position: 'absolute', top: '2px', fontSize: '10px', color: 'orange' }}>N</div>
            {/* Strza≈Çka obracana zgodnie z kierunkiem wiatru */}
            <div style={{
              transform: `rotate(${current.wind_degree}deg)`,
              transition: 'transform 0.5s ease'
            }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <line x1="12" y1="19" x2="12" y2="5"></line>
                <polyline points="5 12 12 5 19 12"></polyline>
              </svg>
            </div>
          </div>
        </div>
        <div style={{marginTop: '10px', fontSize: '13px', opacity: 0.7}}>Kierunek: {current.wind_dir}</div>
      </div>

      {/* 2. Karta Jako≈õci Powietrza i Wilgotno≈õci */}
      <div style={styles.card}>
        <div style={{marginBottom: '15px'}}>
            <div style={styles.title}>Jako≈õƒá Powietrza</div>
            <div style={styles.value}>
                <span style={{color: aqiInfo.color}}>{aqiInfo.text}</span>
            </div>
            {current.air_quality && (
                <div style={{fontSize: '12px', opacity: 0.7, marginTop: '5px'}}>
                    PM2.5: {current.air_quality.pm2_5.toFixed(1)} | PM10: {current.air_quality.pm10.toFixed(1)}
                </div>
            )}
        </div>
        <div style={{borderTop: '1px solid rgba(255,255,255,0.1)', paddingTop: '15px'}}>
             <div style={styles.title}>Wilgotno≈õƒá (Humidity)</div>
             <div style={styles.row}>
                <div style={styles.value}>{current.humidity}%</div>
                <div style={{height: '8px', width: '80px', background: 'rgba(255,255,255,0.1)', borderRadius: '4px', overflow: 'hidden'}}>
                    <div style={{height: '100%', width: `${current.humidity}%`, background: '#3b82f6'}}></div>
                </div>
             </div>
             <div style={{fontSize: '12px', opacity: 0.7, marginTop: '5px'}}>
                Punkt rosy: {current.dewpoint_c}¬∞
             </div>
        </div>
      </div>

      {/* 3. Karta Lifestyle (Inteligentne porady) */}
      <div style={styles.card}>
        <div style={styles.title}>Lifestyle & Porady</div>
        <div style={{display: 'flex', flexDirection: 'column', gap: '5px'}}>
            {getLifestyleTips().map((tip, index) => (
                <div key={index} style={styles.tipItem}>
                    <span style={{fontSize: '20px'}}>{tip.icon}</span>
                    <span>{tip.text}</span>
                </div>
            ))}
        </div>
      </div>

    </div>
  );
};

export default AdditionalInfo;
