import React, { useState, useEffect } from 'react';
import { fetchWeatherData } from './weatherApi';
import WeatherChart from './WeatherChart'; // Tw贸j poprzedni komponent wykresu
import WeatherHighlights from './WeatherHighlights'; // Tw贸j nowy komponent z kartami

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      setError(null);
      try {
        const data = await fetchWeatherData(city);
        setWeatherData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [city]);

  if (loading) return <div style={{color: '#fff', padding: '20px'}}>Wczytywanie...</div>;
  if (error) return <div style={{color: '#f00', padding: '20px'}}>Bd: {error}</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', color: '#fff', marginBottom: '20px' }}>
        <h2 style={{fontSize: '24px'}}>{weatherData.locationName}</h2>
        <div style={{ textAlign: 'right', opacity: 0.8 }}>
           {weatherData.astro.sunrise} |  {weatherData.astro.sunset}
        </div>
      </div>

      {/* 1. SEKCJA WYKRESU */}
      <WeatherChart 
        data={weatherData.chartData} 
        sunrise={weatherData.astro.sunrise} 
        sunset={weatherData.astro.sunset} 
      />

      {/* 2. SEKCJA KART (Twoje WeatherHighlights) */}
      <WeatherHighlights data={weatherData.fullRawData} />
    </div>
  );
};

export default WeatherManager;
