import React from 'react';
import '../shared/styles/style.css';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <footer className="footer glass-panel">
      <div className="footer-content">
        
        <div className="footer-section">
          <p className="copyright">Â© {currentYear} WeatherApp by PavelShap</p>
          <div className="system-status">
            <span className="status-dot"></span>
            System Operational
          </div>
        </div>

        
        <div className="footer-section">
          <p className="data-source">
            Data provided by <a href="https://www.weatherapi.com/" target="_blank" rel="noreferrer">WeatherAPI</a>
          </p>
        </div>

        
        <div className="footer-section social-links">
          <a href="#github" className="social-icon">GitHub</a>
          <a href="#linkedin" className="social-icon">LinkedIn</a>
          <a href="#telegram" className="social-icon">Telegram</a>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
