import React, { useState, useEffect } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import '../shared/styles/style.css';

// --- Компонент КРУПНОЙ иконки ---
const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;

  // Оставляем шаг в 3 часа, чтобы гигантские иконки не слиплись
  if (index % 3 !== 0) return null;

  const iconSize = 50; // Увеличили размер до 50px (было 30)

  return (
    <svg 
      x={cx - iconSize / 2} 
      y={cy - iconSize - 10} // Поднимаем чуть выше над линией
      width={iconSize} 
      height={iconSize} 
      viewBox="0 0 64 64"
    >
      <image 
        href={`https:${payload.icon}`} 
        width="100%" 
        height="100%" 
      />
    </svg>
  );
};

const WeatherChart = ({ data }) => {
  return (
    <div className="chart-wrapper glass-panel">
      <ResponsiveContainer width="100%" height="100%">
        {/* Увеличили top margin до 60, чтобы большие иконки не вылезали за край */}
        <AreaChart data={data} margin={{ top: 60, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis 
            dataKey="time" 
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false}
            axisLine={false}
          />
          <YAxis hide domain={['dataMin - 5', 'dataMax + 5']} />
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          <Tooltip 
            contentStyle={{ backgroundColor: 'rgba(0,0,0,0.8)', border: 'none', borderRadius: '10px' }}
            itemStyle={{ color: '#fff' }}
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={3} // Сделали саму линию чуть толще для баланса
            fillOpacity={1} 
            fill="url(#colorTemp)" 
            name="Temp"
            dot={<CustomizedDot />} 
            // Анимация может "дергать" иконки при загрузке, можно отключить для плавности
            isAnimationActive={false} 
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

// ... остальная часть WeatherManager без изменений ...
