import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, ReferenceDot, Label 
} from 'recharts';

// --- KOMPONENTY POMOCNICZE ---

// Kropka z ikoną pogody (CustomizedDot)
const CustomizedDot = (props) => {
  const { cx, cy, payload, index, interval } = props;

  // Główna logika: jeśli indeks nie dzieli się przez interwał, nie rysuj nic
  if (index % interval !== 0) return null;

  // Opcjonalnie: mniejsze ikony na mniejszych ekranach
  const iconSize = interval >= 4 ? 40 : 48; 

  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 10} width={iconSize} height={iconSize}>
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

// Ikona Słońca/Księżyca dla linii wschodu/zachodu
const SunIcon = ({ x, y, type }) => {
  const iconSize = 34;
  const iconUrl = type === 'sunrise' 
    ? "https://cdn.weatherapi.com/weather/64x64/day/113.png" 
    : "https://cdn.weatherapi.com/weather/64x64/night/113.png";
  
  return (
    <g>
      <circle cx={x} cy={y + 15} r="18" fill="rgba(0,0,0,0.3)" filter="blur(3px)" />
      <svg x={x - iconSize / 2} y={y} width={iconSize} height={iconSize}>
        <image href={iconUrl} width="100%" height="100%" />
      </svg>
    </g>
  );
};

// --- KOMPONENT WYKRESU ---

const WeatherChart = ({ data, sunrise, sunset }) => {
  // 1. Hook do sprawdzania szerokości ekranu
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // 2. Logika ustalania interwału ikon w zależności od szerokości
  let iconInterval = 2; // Domyślnie (duży ekran) co 2 godziny
  let hourInterval = 0; // Domyślnie (duży ekran) każda godzina
  if (windowWidth < 768) {
    iconInterval = 6; // Na telefonie co 6 godzin
    hourInterval = 3; // Co 3 godziny
  }
  else if (windowWidth < 880) {
    
    hourInterval = 2; // Co 3 godziny
  } else if (windowWidth < 1465) {
    iconInterval = 4; // Na laptopie/tablecie co 4 godziny
    hourInterval = 1; // Co 2 godziny
  }

  // 3. Logika czasu "Teraz" i wschodów/zachodów
  const now = new Date();
  const currentFullTime = `${now.getFullYear()}-${(now.getMonth()+1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')} ${now.getHours().toString().padStart(2, '0')}:00`;
  const currentDataPoint = data.find(d => d.fullTime === currentFullTime);

  const formatAstroTime = (timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    const formattedHour = hours.toString().padStart(2, '0');
    const today = new Date().toISOString().split('T')[0];
    const target = `${today} ${formattedHour}:00`;
    return data.some(d => d.fullTime === target) ? target : null;
  };

  const sunX1 = formatAstroTime(sunrise);
  const sunX2 = formatAstroTime(sunset);

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '450px', width: '100%', padding: '20px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 80, right: 20, left: -20, bottom: 20 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0.1}/>
            </linearGradient>
            <linearGradient id="colorFeels" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#82ca9d" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          <XAxis 
            dataKey="fullTime" 
            tickFormatter={(str) => str.split(' ')[1]} 
            stroke="rgba(255,255,255,0.5)" 
            fontSize={10} 
            tickLine={false} 
            axisLine={false} 
            interval={hourInterval} 
            minTickGap={10} 
            dy={10}
          />
          
          <YAxis 
            hide={false}
            orientation="left"
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false} 
            axisLine={false}
            tickFormatter={(val) => `${val}°`}
            domain={['dataMin - 3', 'dataMax + 3']}
          />
          
          {sunX1 && (
            <ReferenceLine x={sunX1} stroke="#ffd700" strokeDasharray="5 5" strokeWidth={2}>
              <Label content={<SunIcon type="sunrise" y={-15} />} />
              <Label value="Sunrise" position="insideTopLeft" fill="#ffd700" fontSize={10} dy={40} dx={5} />
            </ReferenceLine>
          )}

          {sunX2 && (
            <ReferenceLine x={sunX2} stroke="#ff4500" strokeDasharray="5 5" strokeWidth={2}>
              <Label content={<SunIcon type="sunset" y={-15} />} />
              <Label value="Sunset" position="insideTopLeft" fill="#ff4500" fontSize={10} dy={40} dx={5} />
            </ReferenceLine>
          )}

          {currentDataPoint && (
            <ReferenceLine x={currentFullTime} stroke="#00f2ff" strokeWidth={2} strokeDasharray="3 3">
              <Label value="NOW" position="top" fill="#00f2ff" fontSize={12} fontWeight="bold" offset={25} />
            </ReferenceLine>
          )}

          <Tooltip 
            labelFormatter={(label) => `Time: ${label}`}
            contentStyle={{ backgroundColor: 'rgba(26, 26, 26, 0.9)', border: 'none', borderRadius: '10px', color: '#fff' }} 
            itemStyle={{ color: '#fff' }}
          />
          
          {/* PRZEKAZUJEMY dynamiczny interwał do CustomizedDot */}
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            strokeWidth={4} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot interval={iconInterval} />} 
            isAnimationActive={false}
          />

          <Area 
            type="monotone" 
            dataKey="feelsLike" 
            stroke="#82ca9d" 
            strokeWidth={2} 
            strokeDasharray="5 5"
            fill="url(#colorFeels)" 
            isAnimationActive={false}
          />

          {currentDataPoint && (
            <ReferenceDot 
              x={currentFullTime} 
              y={currentDataPoint.temp} 
              r={6} 
              fill="#00f2ff" 
              stroke="#fff" 
              strokeWidth={2} 
            />
          )}
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

// --- GŁÓWNY KOMPONENT MANAGERA ---

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        let query = city.trim().toLowerCase() === 'vinnytsia' ? 'Vinnitsa' : city.trim();
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=2`
        );
        if (!response.ok) throw new Error('City not found');
        const json = await response.json();
        
        setAstro(json.forecast.forecastday[0].astro);
        
        const combinedHours = [
          ...json.forecast.forecastday[0].hour,
          ...json.forecast.forecastday[1].hour
        ];

        const formatted = combinedHours.map(h => ({
          fullTime: h.time,
          time: h.time.split(' ')[1],
          temp: h.temp_c,
          feelsLike: h.feelslike_c,
          icon: h.condition.icon
        }));

        setChartData(formatted);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchWeather();
  }, [city]);

  if (loading) return <div style={{color: '#fff'}}>Loading...</div>;
  if (error) return <div style={{color: '#f00'}}>{error}</div>;

  return (
    <div className="chart-container">
      <div style={{ display: 'flex', justifyContent: 'space-between', color: '#fff', marginBottom: '10px' }}>
        <div style={{ fontSize: '12px', textAlign: 'right' }}>
          Sunrise: {astro.sunrise} | Sunset: {astro.sunset}
        </div>
      </div>
      <div>
        <WeatherChart data={chartData} sunrise={astro.sunrise} sunset={astro.sunset} />
      </div>
    </div>
  );
};

export default WeatherManager;
