import React, { useState, useEffect } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import '../shared/styles/style.css';

// --- Кастомный компонент иконки на графике ---
const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;

  // Рисуем иконку только каждые 3 часа, чтобы не перегружать график
  if (index % 3 !== 0) return null;

  return (
    <svg x={cx - 15} y={cy - 35} width={30} height={30} viewBox="0 0 64 64">
      <image 
        href={`https:${payload.icon}`} 
        width="30" 
        height="30" 
      />
    </svg>
  );
};

// --- Компонент Графика ---
const WeatherChart = ({ data }) => {
  return (
    <div className="chart-wrapper glass-panel">
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 40, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <XAxis 
            dataKey="time" 
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false}
            axisLine={false}
          />
          <YAxis 
            hide // Скроем ось Y для более чистого "дизайнерского" вида, либо оставь как было
          />
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          <Tooltip 
            contentStyle={{ backgroundColor: 'rgba(0,0,0,0.8)', border: 'none', borderRadius: '10px' }}
            itemStyle={{ color: '#fff' }}
            // Добавляем отображение условий в тултип
            formatter={(value, name, props) => [
              `${value}° (${props.payload.condition})`, 
              "Temp"
            ]}
          />
          
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            fillOpacity={1} 
            fill="url(#colorTemp)" 
            name="Temp"
            // Вставляем нашу кастомную иконку
            dot={<CustomizedDot />} 
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

// --- Основной Компонент ---
const WeatherManager = ({ city = 'Lublin' }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const getWeatherData = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${city}&days=1&aqi=no&alerts=no`
        );
        if (!response.ok) throw new Error('City not found');
        
        const json = await response.json();
        
        const formattedData = json.forecast.forecastday[0].hour.map((h) => ({
          time: h.time.split(' ')[1], 
          temp: h.temp_c,
          condition: h.condition.text,
          icon: h.condition.icon, // Ссылка на иконку
          chanceOfRain: h.chance_of_rain,
          chanceOfSnow: h.chance_of_snow
        }));

        setChartData(formattedData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    getWeatherData();
  }, [city]);

  if (loading) return <div className="weather-manager">Loading chart...</div>;
  if (error) return <div className="weather-manager">Error: {error}</div>;

  return (
    <div className="weather-manager">
      <h2>Next 24 hours: {city}</h2>
      <WeatherChart data={chartData} />
    </div>
  );
};

export default WeatherManager;
