import React, { useState, useEffect } from 'react';
import { 
  AreaChart, Area, XAxis, YAxis, CartesianGrid, 
  Tooltip, ResponsiveContainer, ReferenceLine, ReferenceDot, Label 
} from 'recharts';

// Custom Weather Icons (2x size, every 2 hours)
const CustomizedDot = (props) => {
  const { cx, cy, payload, index } = props;
  if (index % 2 !== 0) return null; 
  const iconSize = 64; 
  return (
    <svg x={cx - iconSize / 2} y={cy - iconSize - 15} width={iconSize} height={iconSize}>
      <image href={`https:${payload.icon}`} width="100%" height="100%" />
    </svg>
  );
};

// Custom Sun/Moon Icons for exact Sunrise/Sunset spots
const SunIcon = ({ x, y, type }) => {
  const iconSize = 40;
  // Using specific WeatherAPI icons for Sun and Moon
  const iconUrl = type === 'sunrise' 
    ? "https://cdn.weatherapi.com/weather/64x64/day/113.png" 
    : "https://cdn.weatherapi.com/weather/64x64/night/113.png";
  
  return (
    <g>
      <circle cx={x} cy={y + 20} r="22" fill="rgba(0,0,0,0.3)" filter="blur(4px)" />
      <svg x={x - iconSize / 2} y={y} width={iconSize} height={iconSize}>
        <image href={iconUrl} width="100%" height="100%" />
      </svg>
    </g>
  );
};

const WeatherChart = ({ data, sunrise, sunset }) => {
  const now = new Date();
  const currentHourX = `${now.getHours().toString().padStart(2, '0')}:00`;
  const currentDataPoint = data.find(d => d.time === currentHourX);

  // Helper to align AM/PM API time to the 00:00 X-Axis scale
  const formatAstroTime = (timeStr) => {
    if (!timeStr) return null;
    const [time, modifier] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    if (hours === '12') hours = '00';
    if (modifier === 'PM') hours = parseInt(hours, 10) + 12;
    // We match the nearest hour for the vertical line to align with data points
    return `${hours.toString().padStart(2, '0')}:00`; 
  };

  const sunX1 = formatAstroTime(sunrise);
  const sunX2 = formatAstroTime(sunset);

  return (
    <div className="chart-wrapper glass-panel" style={{ height: '450px', width: '100%', padding: '20px' }}>
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 80, right: 20, left: -20, bottom: 20 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0.1}/>
            </linearGradient>
            <linearGradient id="colorFeels" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#82ca9d" stopOpacity={0}/>
            </linearGradient>
          </defs>
          
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          
          <XAxis 
            dataKey="time" 
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false} 
            axisLine={false} 
            dy={10}
          />
          
          {/* VISIBLE Y-AXIS SCALE */}
          <YAxis 
            orientation="left"
            stroke="rgba(255,255,255,0.5)" 
            fontSize={12} 
            tickLine={false} 
            axisLine={false}
            tickFormatter={(value) => `${value}°`}
            domain={['dataMin - 5', 'dataMax + 5']}
          />
          
          {/* Sunrise Vertical Line + Icon */}
          {sunX1 && (
            <ReferenceLine x={sunX1} stroke="#ffd700" strokeDasharray="5 5" strokeWidth={2}>
              <Label content={<SunIcon type="sunrise" y={-10} />} />
              <Label value="Sunrise" position="insideTopLeft" fill="#ffd700" fontSize={10} dy={45} dx={5} />
            </ReferenceLine>
          )}

          {/* Sunset Vertical Line + Icon */}
          {sunX2 && (
            <ReferenceLine x={sunX2} stroke="#ff4500" strokeDasharray="5 5" strokeWidth={2}>
              <Label content={<SunIcon type="sunset" y={-10} />} />
              <Label value="Sunset" position="insideTopLeft" fill="#ff4500" fontSize={10} dy={45} dx={5} />
            </ReferenceLine>
          )}

          {/* NOW Vertical Line */}
          {currentDataPoint && (
            <ReferenceLine x={currentHourX} stroke="#00f2ff" strokeWidth={2} strokeDasharray="3 3">
              <Label value="NOW" position="top" fill="#00f2ff" fontSize={12} fontWeight="bold" offset={25} />
            </ReferenceLine>
          )}

          <Tooltip 
            contentStyle={{ backgroundColor: 'rgba(26, 26, 26, 0.9)', border: 'none', borderRadius: '10px', color: '#fff' }} 
            itemStyle={{ color: '#fff' }}
          />
          
          {/* Actual Temperature */}
          <Area 
            type="monotone" 
            dataKey="temp" 
            name="Temp"
            stroke="#8884d8" 
            strokeWidth={4} 
            fill="url(#colorTemp)" 
            dot={<CustomizedDot />}
            isAnimationActive={false}
          />

          {/* Feels Like Temperature */}
          <Area 
            type="monotone" 
            dataKey="feelsLike" 
            name="Feels Like"
            stroke="#82ca9d" 
            strokeWidth={2} 
            strokeDasharray="5 5"
            fill="url(#colorFeels)" 
            isAnimationActive={false}
          />

          {/* Current Position Dot */}
          {currentDataPoint && (
            <ReferenceDot 
              x={currentHourX} 
              y={currentDataPoint.temp} 
              r={6} 
              fill="#00f2ff" 
              stroke="#fff" 
              strokeWidth={2} 
            />
          )}
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

const WeatherManager = ({ city = 'Vinnytsia' }) => {
  const [chartData, setChartData] = useState([]);
  const [astro, setAstro] = useState({ sunrise: '', sunset: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001';

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        setLoading(true);
        let query = city.trim().toLowerCase() === 'vinnytsia' ? 'Vinnitsa' : city.trim();
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${encodeURIComponent(query)}&days=2`
        );
        if (!response.ok) throw new Error('City not found');
        const json = await response.json();
        
        setAstro(json.forecast.forecastday[0].astro);
        setChartData(json.forecast.forecastday[0].hour.map(h => ({
          time: h.time.split(' ')[1],
          temp: h.temp_c,
          feelsLike: h.feelslike_c,
          icon: h.condition.icon
        })));
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchWeather();
  }, [city]);

  if (loading) return <div className="glass-panel" style={{padding: '20px', color: '#fff'}}>Loading Weather...</div>;
  if (error) return <div className="glass-panel" style={{padding: '20px', color: '#ff4d4d'}}>Error: {error}</div>;

  return (
    <div className="weather-container" style={{ padding: '20px', fontFamily: 'sans-serif' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end', marginBottom: '10px', color: '#fff' }}>
         <div>
            <h1 style={{ margin: 0, fontWeight: '300', fontSize: '28px' }}>{city}</h1>
            <p style={{ margin: 0, opacity: 0.6, fontSize: '14px' }}>Hourly Forecast</p>
         </div>
         <div style={{ textAlign: 'right', fontSize: '13px', borderLeft: '1px solid rgba(255,255,255,0.2)', paddingLeft: '15px' }}>
            <span style={{ color: '#ffd700' }}>Sunrise: {astro.sunrise}</span><br/>
            <span style={{ color: '#ff4500' }}>Sunset: {astro.sunset}</span>
         </div>
      </div>
      <WeatherChart data={chartData} sunrise={astro.sunrise} sunset={astro.sunset} />
    </div>
  );
};

export default WeatherManager;
