import React, { useState, useEffect } from 'react';
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer
} from 'recharts';

// --- 1. Компонент Графика (Презентация) ---
const WeatherChart = ({ data }) => {
  return (
    <div style={{ width: '100%', height: 300, marginTop: '20px' }} className="glass-panel">
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorTemp" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8}/>
              <stop offset="95%" stopColor="#8884d8" stopOpacity={0}/>
            </linearGradient>
          </defs>
          <XAxis 
            dataKey="time" 
            stroke="#ccc" 
            fontSize={12} 
            tickLine={false}
          />
          <YAxis 
            stroke="#ccc" 
            fontSize={12} 
            unit="°" 
            tickLine={false}
            axisLine={false}
          />
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(255,255,255,0.1)" />
          <Tooltip 
            contentStyle={{ backgroundColor: '#222', border: 'none', borderRadius: '8px', color: '#fff' }}
          />
          <Area 
            type="monotone" 
            dataKey="temp" 
            stroke="#8884d8" 
            fillOpacity={1} 
            fill="url(#colorTemp)" 
            name="Температура"
          />
          <Area 
            type="monotone" 
            dataKey="feelsLike" 
            stroke="#82ca9d" 
            fill="transparent" 
            name="Ощущается как"
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

// --- 2. Основной Компонент (Логика и Фильтрация) ---
const WeatherManager = ({ city = 'Lublin' }) => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '86f7352254b44be6841171331263001'; // Твой ключ

  useEffect(() => {
    const getWeatherData = async () => {
      try {
        setLoading(true);
        const response = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${city}&days=1&aqi=no&alerts=no`
        );
        
        if (!response.ok) throw new Error('Ошибка при загрузке данных');
        
        const json = await response.json();

        // --- ФИЛЬТРАЦИЯ ДАННЫХ ---
        // Превращаем сырой массив hour в формат для графика
        const formattedData = json.forecast.forecastday[0].hour.map((h) => ({
          // Берем только часы и минуты из строки "2026-02-14 00:00"
          time: h.time.split(' ')[1], 
          temp: h.temp_c,
          feelsLike: h.feelslike_c,
          condition: h.condition.text
        }));

        setChartData(formattedData);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    getWeatherData();
  }, [city]);

  if (loading) return <div>Загрузка графика...</div>;
  if (error) return <div>Ошибка: {error}</div>;

  return (
    <div className="weather-manager">
      <h2>Next 24hours: {city}</h2>
      <WeatherChart data={chartData} />
      
      
    </div>
  );
};

export default WeatherManager;
